import React from 'react'
import { withRouter } from 'react-router-dom'
import axios from 'axios'

class RoleAbilities extends React.Component {
  constructor() {
    super()

    this.state = {
      ability: []
    }

  }
  async componentDidMount() {
    try {
      const res = await axios (`https://www.dnd5eapi.co/api${this.props.match.url.toLowerCase()}/levels/1`)
      this.setState(res.data)
      console.log(res.data)

    } catch (error) {
      console.log(error)
    }    
  }

  render() {
    if (this.state == null) {
      return "Loading..."
    }
    return (<>
      <h3>{this.state.class.name} level 1 class abilities</h3>

      {this.state.features.map(feature => <p>{feature.name}</p>)}


  </>)
  }

}

export default withRouter(RoleAbilities)
