import React from 'react'
import { withRouter } from 'react-router'

function AbilityDetails(props) {

  return (
    <div>
      {props.details.map(info =>
        <div>
          <p id>{info.data.name}</p>
          <p>{info.data.desc}<br /></p>
          </div>)}
    </div>
  )
}

export default withRouter(AbilityDetails)






















