import React from 'react'
import Route, { withRouter } from 'react-router-dom'
import axios from 'axios'

class RoleAbilities extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      abilities: []
    }
  }
  async componentDidMount() {
    try {
      const res = await axios(`https://www.dnd5eapi.co/api${this.props.match.url.toLowerCase()}/levels/1`)
      const info = res.data

      this.setState({
        abilities: info
      })
      console.log(this.state.abilities.features)


    } catch (error) {
      console.log(error)
    }    
  }
  render() {
    if (this.state == null) {
      return "Loading..."
    }
    return (<div id="abilities" className="role-cards">
      <h3>Level 1 {this.props.match.params.index} abilities</h3>
      <p>Ability test: {this.state.abilities.features}</p>

      {/* {this.state.abilities.map(name => <p key={name.name}>{name.name}</p>)} */}

  </div>)
  }

}

export default withRouter(RoleAbilities)
