import React from 'react'
import { withRouter } from 'react-router-dom'
import axios from 'axios'

class RoleEquipment extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      name: props.name
    }
  }

  async componentDidMount() {
    try {
      const res = await axios (`https://www.dnd5eapi.co/api${this.props.match.url.toLowerCase()}/starting-equipment`)
      this.setState(res.data)
      console.log(res.data)

    } catch (error) {
      console.log(error)
    }    
  }



  render() {
    if (this.state == null) {
      return `Loading ${this.props.match.params.naime}`
    }
  return(
    <div>

        {this.props.match.params.index}
    </div>
  )
  }
}

export default withRouter(RoleEquipment)
